import streamlit as st

#halaman utama
st.title('aplikasi perhitungan luas bangun datarrrrrr')
st.header('ini buatan anak SI')

menu = st.sidebar.selectbox('bentuk apa?', ['luas persegi', 'luas segitiga', 'luas lingkaran', 'persegi panjang'])

if menu == 'luas persegi':
    st.write('ini halaman unutk menghitung luas persegi :balloon:')
    st.markdown(':red[ini warna merah]')
    st.image('data:image/png;base64,', caption='papapa')
    def LuasPersegi(s):
        return s * s
    sisi = st.number_input('masukan sisi', min_value=0)
    if st.button('hitung luas'):
        luas = LuasPersegi(sisi)
        st.success(f'luas persegi adalah {luas}')
        st.write(f'di atas adalah hasilnya, yaitu {luas}')

#ini menu buat pilih segitiga yang di sebelah kiri
elif menu == 'luas segitiga':
    st.write('ini halaman unutk menghitung luas :red[segitiga:]')
    st.markdown(':blue[ini warna biru segitiga]')
    st.image('https://awsimages.detik.net.id/community/media/visual/2023/01/30/contoh-soal-segitiga-sama-kaki-2_169.png?w=620', caption='gambar rumus segitiga')
    #ini rumus luas segitiga
    def LuasSegitiga(a, t):
        return 0.5 * a * t
    alas = st.number_input('masukan alas', min_value=0)
    tinggi = st.number_input('masukan tinggi', min_value=0)
    if st.button('hitung luas segitiga'):
        luas = LuasSegitiga(alas, tinggi)
        st.success(f'luas segitiga adalah {luas}')
        st.write(f'di atas adalah hasilnya, yaitu {luas}')

#ini menu buat pilih lingkaran yang di sebelah kiri
elif menu == 'luas lingkaran':
    st.write('ini halaman unutk menghitung luas :red[Lingkaran:]')
    st.markdown(':green[ini warna ijo]')
    st.image('data:image/png;base64,', caption='gambar rumus lingkaran')
    #ini rumus luas lingkaran
    def LuasLingkaran(r):
        return 3.14 * r * r
    jari = st.number_input('masukan jari-jari', min_value=0)
    if st.button('hitung luas lingkaran'):
        luas = LuasLingkaran(jari)
        st.success(f'luas lingkaran adalah {luas}')
        st.write(f'di atas adalah hasilnya, yaitu {luas}')

#persegi panjang
elif menu == 'persegi panjang':
    st.write('ini halaman unutk menghitung luas :red[persegi panjang:]')
    st.markdown(':orange[ini warna orens persegi panjang]')
    st.image('data:image/png;base64,', caption='gambar rumus persegi panjang')
    #ini rumus luas persegi panjang
    def LuasPersegiPanjang(p, l):
        return p * l
    panjang = st.number_input('masukan panjang', min_value=0)
    lebar = st.number_input('masukan lebar', min_value=0)
    if st.button('hitung luas persegi panjang'):
        luas = LuasPersegiPanjang(panjang, lebar)
        st.success(f'luas persegi panjang adalah {luas}')
        st.write(f'di atas adalah hasilnya, yaitu {luas}')
